#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <cmath>
using namespace std;
#define endl '\n'
#define ll long long
#define MODULO 1'000'000'007
#define MAX 200'000
int N;
string s;
vector<int> presum(MAX + 1, 0); // 0~i까지의 W 갯수
vector<int> postsum(MAX + 1, 0); // i~N 까지 E의 갯수
vector<ll> dp(MAX + 1, 0);
ll get2Pow(int power) {
	if (power == 0) {
		return dp[power] = 1;
	}
	if (power == 1) return dp[power] = 2;
	if (dp[power] != 0) return dp[power];

	if (power & 1) return dp[power] = (((get2Pow(power / 2) * get2Pow(power / 2)) % MODULO) * 2) % MODULO;
	else return dp[power] = (get2Pow(power / 2) * get2Pow(power / 2)) % MODULO;
}
void Solve() {
	int answer = 0;
	for (int i = 0; i < s.size(); i++) {
		if (s[i] == 'H') {
			// 2^N -  N - 1
			// 양수
			answer += ((ll)presum[i] * ((get2Pow(postsum[i]) - postsum[i] - 1) % MODULO)) % MODULO;
			answer %= MODULO;
		}
	}
	cout << answer << endl;
}
void init() {
	ios_base::sync_with_stdio(0);
	cin.tie(0);
	cin >> N >> s;


	if (s[0] == 'W') presum[0] = 1;

	for (int i = 1; i < s.size(); i++) {
		presum[i] = presum[i - 1];
		if (s[i] == 'W') presum[i]++;
	}

	if (s.back() == 'E') postsum[N - 1] = 1;

	for (int i = N - 2; i >= 0; i--) {
		postsum[i] = postsum[i + 1];
		if (s[i] == 'E') postsum[i]++;
	}

	get2Pow(MAX);
}
int main() {
	init();
	Solve();

	return 0;
}


